import React, { Component } from 'react';
import './App.css';
import FlowPanel from './components/FlowPanel';
import Controller from './components/Controller';
import Table from './components/Table';

class App extends Component {

  constructor(props){
    super(props);
    this.state = {
      tasks : [],
      isDisplayed : false
    }

    this.onGenerateData = this.onGenerateData.bind(this);
  }

  componentWillMount(){
    if(localStorage && localStorage.getItem('tasks')){
      var tasks = JSON.parse(localStorage.getItem('tasks'));

      this.setState({
        tasks : tasks
      });
    }
  }

  onToggleForm(){
    this.setState({
      isDisplayed : !this.state.isDisplayed
    });
  }

  onClose(){
    this.setState({
      isDisplayed : false
    })
  }

  onSubmit(data){
    var { tasks } = this.state; 

    data.id = this.generateRandomID();

    tasks.push(data);

    this.setState({
      tasks : tasks
    });

    localStorage.setItem('tasks', JSON.stringify(tasks));
  }

  render() {

    var { tasks, isDisplayed } = this.state; // var tasks = this.state.tasks
    var eleFPanel = isDisplayed ? 
      <FlowPanel 
        onToggleForm = {this.onToggleForm.bind(this)}
        onClose = {this.onClose.bind(this)}
        onSubmit = {this.onSubmit.bind(this)}
      /> 
      : '';

    return (
      <div className="container">
        <div className = "Header">
          <h2 align="center">Work management</h2>
        </div>
        <br/>
        <div className = "Body">
          <div className="row">
            <div className={ isDisplayed ? "col-xs-4 col-sm-4 col-md-4 col-lg-4" 
              : '' }>
              {eleFPanel}
            </div>
            <div className={ isDisplayed ? "col-xs-8 col-sm-8 col-md-8 col-lg-8" 
              : "col-xs-12 col-sm-12 col-md-12 col-lg-12" }
            >
              <Controller 
                onGenerateData = {this.onGenerateData} 
                isDisplayed = {isDisplayed} 
                onToggleForm = {this.onToggleForm.bind(this)}
              />
              <br/>
              <div className="row">
                <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
                  <Table tasks={tasks}/>
                </div>
              </div>           
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
