import React, { Component } from 'react';
import './App.css';
import FlowPanel from './components/FlowPanel';
import Controller from './components/Controller';
import Table from './components/Table';

class App extends Component {

  constructor(props){
    super(props);
    this.state = {
      tasks : []
    }

    this.onGenerateData = this.onGenerateData.bind(this);
  }

  onGenerateData(){
    var tasks = [
      {
        id : this.generateRandomID(),
        name : 'Checkout',
        date : '25/10/2018',
        status : true
      },
      {
        id : this.generateRandomID(),
        name : 'Checkin',
        date : '26/10/2018',
        status : false
      },
      {
        id : this.generateRandomID(),
        name : 'Checkout',
        date : '26/10/2018',
        status : false
      },
    ];

    this.setState({
      tasks : tasks
    });

    this.localStorage.setItem('tasks',tasks);
  }

  random(){
    return Math.floor((1+Math.random()) * 0x1000).toString(16).substring(1);
  }

  generateRandomID(){
    return this.random() 
      + '-' + this.random() + this.random() + this.random() 
      + '-' + this.random() + this.random() 
      + '-' + this.random() 
      + '-' + this.random()
    ;
  }

  render() {
    return (
      <div className="container">
        <div className = "Header">
          <h2 align="center">Work management</h2>
        </div>
        <br/>
        <div className = "Body">
          <div className="row">
            <div className="col-xs-4 col-sm-4 col-md-4 col-lg-4">
              <FlowPanel />
            </div>
            <div className="col-xs-8 col-sm-8 col-md-8 col-lg-8">
              <Controller onGenerateData={this.onGenerateData}/>
              <br/>
              <div className="row">
                <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
                  <Table />
                </div>
              </div>           
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
