import React, { Component } from 'react';
import TaskItem from './TaskItem';

class Table extends Component {
  render() {
    var {tasks} = this.props;
    var eleTasks = tasks.map((task, index) => {
      return <TaskItem 
                key={index} 
                task={task} 
                onChangeStatus={this.props.onChangeStatus}
              />
    })

    return (
      <div className="row">
        <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
          <table className="table table-bordered table-hover">
            <thead>
              <tr>
                <th className="text-center">No.</th>
                <th className="text-center">Task name</th>
                <th className="text-center">Date</th>
                <th className="text-center">Status</th>
                <th className="text-center">Actions</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td></td>
                <td>
                  <input type="text" name="filterName" className="form-control" />
                </td>
                <td>
                  <select name="filterDate" className="form-control">
                    <option value={-1}>All</option>
                    <option value={0}>Latest</option>
                    <option value={1}>Earliest</option>
                  </select>
                </td>
                <td>
                  <select name="filterStatus" className="form-control">
                    <option value={-1}>All</option>
                    <option value={0}>Activated</option>
                    <option value={1}>Deactivated</option>
                  </select>
                </td>
                <td></td>
              </tr>
                {eleTasks}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}

export default Table;
